mapper.insert:
insert into activity_count (id, owner_no, activity_name, 
  activity_type, image_name, status, 
  create_time, update_time, descs
  )
values (#{id,jdbcType=BIGINT}, #{ownerNo,jdbcType=VARCHAR}, #{activityName,jdbcType=VARCHAR}, 
  #{activityType,jdbcType=VARCHAR}, #{imageName,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{descs,jdbcType=VARCHAR}
  )

mapper.select:
	select 
	from activity_count T WHERE 1 = 1
    
      AND owner_no = #{ownerNo,jdbcType=VARCHAR}
    
    
      AND activity_name = #{activityName,jdbcType=VARCHAR}
    
    
      AND activity_type = #{activityType,jdbcType=VARCHAR}
    
    
      AND image_name = #{imageName,jdbcType=VARCHAR}
    
    
      AND status = #{status,jdbcType=VARCHAR}
    
    
      AND create_time = #{createTime,jdbcType=TIMESTAMP}
    
    
      AND update_time = #{updateTime,jdbcType=TIMESTAMP}
    
    
      AND descs = #{descs,jdbcType=VARCHAR}
