mapper.insert:
insert into article (id, title, content_name, 
  icon_name, article_type, tag, 
  source, read_count, create_time, 
  update_time, status, cretaer
  )
values (#{id,jdbcType=BIGINT}, #{title,jdbcType=VARCHAR}, #{contentName,jdbcType=VARCHAR}, 
  #{iconName,jdbcType=VARCHAR}, #{articleType,jdbcType=VARCHAR}, #{tag,jdbcType=VARCHAR}, 
  #{source,jdbcType=VARCHAR}, #{readCount,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{status,jdbcType=VARCHAR}, #{cretaer,jdbcType=VARCHAR}
  )

mapper.select:
	select 
	from article T WHERE 1 = 1
    
      AND title = #{title,jdbcType=VARCHAR}
    
    
      AND content_name = #{contentName,jdbcType=VARCHAR}
    
    
      AND icon_name = #{iconName,jdbcType=VARCHAR}
    
    
      AND article_type = #{articleType,jdbcType=VARCHAR}
    
    
      AND tag = #{tag,jdbcType=VARCHAR}
    
    
      AND source = #{source,jdbcType=VARCHAR}
    
    
      AND read_count = #{readCount,jdbcType=INTEGER}
    
    
      AND create_time = #{createTime,jdbcType=TIMESTAMP}
    
    
      AND update_time = #{updateTime,jdbcType=TIMESTAMP}
    
    
      AND status = #{status,jdbcType=VARCHAR}
    
    
      AND cretaer = #{cretaer,jdbcType=VARCHAR}
