mapper.select:
	select 
	from draw T WHERE 1 = 1
    
      AND owner_no = #{ownerNo,jdbcType=VARCHAR}
    
    
      AND activity_id = #{activityId,jdbcType=BIGINT}
    
    
      AND prize_id = #{prizeId,jdbcType=BIGINT}
    
    
      AND create_date = #{createDate,jdbcType=DATE}
    
    
      AND create_time = #{createTime,jdbcType=TIMESTAMP}
    
    
      AND is_enter = #{isEnter,jdbcType=VARCHAR}
    
    
      AND is_enter_descs = #{isEnterDescs,jdbcType=VARCHAR}
