mapper.select:
	select 
	from prize T WHERE 1 = 1
    
      AND owner_no = #{ownerNo,jdbcType=VARCHAR}
    
    
      AND prize_name = #{prizeName,jdbcType=VARCHAR}
    
    
      AND prize_icon_name = #{prizeIconName,jdbcType=VARCHAR}
    
    
      AND prize_image_name = #{prizeImageName,jdbcType=VARCHAR}
    
    
      AND prize_count = #{prizeCount,jdbcType=INTEGER}
    
    
      AND prize_balance = #{prizeBalance,jdbcType=INTEGER}
    
    
      AND create_time = #{createTime,jdbcType=TIMESTAMP}
    
    
      AND update_time = #{updateTime,jdbcType=TIMESTAMP}
    
    
      AND is_enter = #{isEnter,jdbcType=VARCHAR}
    
    
      AND status = #{status,jdbcType=VARCHAR}
